// Process video with ffmpeg for Telegram inline streaming
const fs = require('fs');
const { exec } = require('child_process');
const path = require('path');
const crypto = require('crypto');

// Get input data
const inputData = $input.all();
const videoFile = inputData[0].binary;
const originalName = inputData[0].json.name;

// Generate unique filename
const timestamp = Date.now();
const randomId = crypto.randomBytes(8).toString('hex');
const inputPath = `/tmp/input_${timestamp}_${randomId}.${path.extname(originalName)}`;
const outputPath = `/tmp/output_${timestamp}_${randomId}.mp4`;
const thumbnailPath = `/tmp/thumb_${timestamp}_${randomId}.jpg`;

try {
  // Write input file
  fs.writeFileSync(inputPath, Buffer.from(videoFile.data, videoFile.fileExtension === 'base64' ? 'base64' : 'binary'));

  // FFmpeg command for Telegram optimization
  // Key flags:
  // -movflags +faststart: Move moov atom to beginning for streaming
  // -c:v libx264: Ensure H.264 codec compatibility
  // -c:a aac: Ensure AAC audio codec
  // -preset fast: Balance between speed and compression
  const ffmpegCmd = `ffmpeg -i "${inputPath}" -c:v libx264 -c:a aac -preset fast -movflags +faststart -y "${outputPath}"`;

  // Generate thumbnail command
  const thumbnailCmd = `ffmpeg -i "${inputPath}" -ss 00:00:01 -vframes 1 -q:v 2 -y "${thumbnailPath}"`;

  // Execute ffmpeg for video processing
  const videoResult = await new Promise((resolve, reject) => {
    exec(ffmpegCmd, { timeout: 300000 }, (error, stdout, stderr) => {
      if (error) {
        console.error('FFmpeg video error:', error);
        console.error('FFmpeg stderr:', stderr);
        reject(error);
      } else {
        resolve({ stdout, stderr });
      }
    });
  });

  // Execute ffmpeg for thumbnail generation
  const thumbResult = await new Promise((resolve, reject) => {
    exec(thumbnailCmd, { timeout: 60000 }, (error, stdout, stderr) => {
      if (error) {
        console.error('FFmpeg thumbnail error:', error);
        console.error('FFmpeg stderr:', stderr);
        // Don't reject - thumbnail is optional
        resolve(null);
      } else {
        resolve({ stdout, stderr });
      }
    });
  });

  // Read processed files
  const processedVideo = fs.readFileSync(outputPath);
  let thumbnailData = null;

  if (thumbResult && fs.existsSync(thumbnailPath)) {
    thumbnailData = fs.readFileSync(thumbnailPath);
  }

  // Clean up temporary files
  [inputPath, outputPath, thumbnailPath].forEach(filePath => {
    try {
      if (fs.existsSync(filePath)) {
        fs.unlinkSync(filePath);
      }
    } catch (e) {
      console.warn('Failed to cleanup file:', filePath, e.message);
    }
  });

  // Return processed data
  return {
    json: {
      originalName,
      processedName: `processed_${originalName}`,
      originalSize: videoFile.data.length,
      processedSize: processedVideo.length,
      hasThumbnail: !!thumbnailData,
      ffmpegOutput: videoResult.stderr
    },
    binary: {
      video: {
        data: processedVideo,
        mimeType: 'video/mp4',
        fileExtension: 'mp4',
        fileName: `processed_${path.parse(originalName).name}.mp4`
      },
      thumbnail: thumbnailData ? {
        data: thumbnailData,
        mimeType: 'image/jpeg',
        fileExtension: 'jpg',
        fileName: `thumb_${path.parse(originalName).name}.jpg`
      } : null
    }
  };

} catch (error) {
  // Clean up files on error
  [inputPath, outputPath, thumbnailPath].forEach(filePath => {
    try {
      if (fs.existsSync(filePath)) {
        fs.unlinkSync(filePath);
      }
    } catch (e) {
      // Ignore cleanup errors
    }
  });

  throw new Error(`Video processing failed: ${error.message}`);
}

// Log processing results and prepare summary
const items = $input.all();

return items.map(item => ({
  json: {
    ...item.json,
    processingComplete: true,
    timestamp: new Date().toISOString(),
    summary: {
      originalFile: item.json.originalName,
      processedSize: `${Math.round(item.json.processedSize / 1024 / 1024 * 100) / 100} MB`,
      hasThumbnail: item.json.hasThumbnail,
      streamingEnabled: true,
      faststartApplied: true
    }
  }
}));
