const items = $input.all();

for (const item of items) {
  // Write binary data to temp file for ffmpeg processing
  const fs = require('fs');
  const path = require('path');

  if (item.binary && item.binary.videoData) {
    const inputPath = '/tmp/input_video_' + item.json.id;
    const outputPath = '/tmp/output_video_' + item.json.id + '.mp4';

    // Write input file
    fs.writeFileSync(inputPath, Buffer.from(item.binary.videoData.data, 'base64'));

    // Store paths for ffmpeg command
    item.json.inputPath = inputPath;
    item.json.outputPath = outputPath;

    // Add telegram chat ID (you should configure this)
    item.json.telegram_chat_id = process.env.TELEGRAM_CHAT_ID || '@your_channel_id';
  }
}

return items;

// Process video with ffmpeg and read result
const items = $input.all();
const fs = require('fs');
const { execSync } = require('child_process');

for (const item of items) {
  if (item.json.inputPath && item.json.outputPath) {
    try {
      // Execute ffmpeg command for faststart
      const command = `ffmpeg -i "${item.json.inputPath}" -movflags +faststart -c copy -y "${item.json.outputPath}"`;
      execSync(command);

      // Read processed video
      const processedVideo = fs.readFileSync(item.json.outputPath);

      // Update binary data with processed video
      item.binary.videoData = {
        data: processedVideo.toString('base64'),
        mimeType: 'video/mp4',
        fileName: item.json.name + '_processed.mp4',
        fileSize: processedVideo.length
      };

      // Cleanup temp files
      fs.unlinkSync(item.json.inputPath);
      fs.unlinkSync(item.json.outputPath);

    } catch (error) {
      // If ffmpeg fails, use original video
      console.log('FFmpeg processing failed, using original video:', error.message);
    }
  }
}

return items;

