// Validate and filter video files
const item = $input.first()?.json;

if (!item) {
  return [];
}

// Supported video formats
const supportedFormats = ['.mp4', '.avi', '.mov', '.mkv', '.webm', '.flv', '.m4v'];
const fileName = item.name || '';
const fileSize = parseInt(item.size) || 0;

// Check file extension
const isVideoFile = supportedFormats.some(format => 
  fileName.toLowerCase().endsWith(format)
);

// Check file size (max 2GB for processing)
const maxSize = 2 * 1024 * 1024 * 1024; // 2GB
const isValidSize = fileSize > 0 && fileSize <= maxSize;

if (isVideoFile && isValidSize) {
  return [{
    json: {
      ...item,
      processing: {
        startTime: new Date().toISOString(),
        status: 'validated',
        originalSize: fileSize,
        sizeInMB: Math.round(fileSize / 1048576 * 100) / 100
      }
    }
  }];
} else {
  // Log rejected files
  console.log(`Rejected file: ${fileName}, Size: ${fileSize}, IsVideo: ${isVideoFile}, ValidSize: ${isValidSize}`);
  return [];
}

// Generate unique file paths and prepare for processing
const item = $input.first()?.json;
const binary = $input.first()?.binary?.video_data;

if (!binary) {
  throw new Error('No video data found');
}

const timestamp = Date.now();
const randomId = Math.random().toString(36).substring(2, 8);
const baseName = `video_${timestamp}_${randomId}`;
const originalExt = binary.fileName?.split('.').pop() || 'mp4';

const paths = {
  original: `/tmp/${baseName}.${originalExt}`,
  optimized: `/tmp/${baseName}_optimized.mp4`,
  thumbnail: `/tmp/${baseName}_thumb.jpg`,
  info: `/tmp/${baseName}_info.json`
};

return [{
  json: {
    ...item,
    processing: {
      ...item.processing,
      paths,
      baseName,
      timestamp,
      step: 'file_paths_prepared'
    }
  },
  binary: {
    video_data: binary
  }
}];

// Determine optimal FFmpeg settings based on video analysis
const item = $input.first()?.json;
let videoInfo = {};

try {
  const infoData = $input.first()?.binary?.data?.data;
  if (infoData) {
    videoInfo = JSON.parse(Buffer.from(infoData, 'base64').toString());
  }
} catch (e) {
  console.log('Could not parse video info, using defaults');
}

const format = videoInfo.format || {};
const videoStream = videoInfo.streams?.find(s => s.codec_type === 'video') || {};
const audioStream = videoInfo.streams?.find(s => s.codec_type === 'audio') || {};

// Determine if re-encoding is needed
const duration = parseFloat(format.duration) || 0;
const bitrate = parseInt(format.bit_rate) || 0;
const width = parseInt(videoStream.width) || 0;
const height = parseInt(videoStream.height) || 0;
const isH264 = videoStream.codec_name === 'h264';
const hasAudio = audioStream.codec_name !== undefined;

// Calculate target settings
let ffmpegCommand = '';
let needsReencoding = false;

// Check if file needs optimization for Telegram
const telegramMaxSize = 50 * 1024 * 1024; // 50MB
const currentSize = parseInt(item.processing.originalSize) || 0;

if (currentSize > telegramMaxSize || !isH264 || width > 1920 || height > 1080) {
  needsReencoding = true;

  // Calculate CRF based on original size
  let crf = 23;
  if (currentSize > telegramMaxSize * 2) crf = 28;
  else if (currentSize > telegramMaxSize * 1.5) crf = 25;

  ffmpegCommand = `ffmpeg -i "{{ $json.processing.paths.original }}" -c:v libx264 -crf ${crf} -preset medium -c:a aac -movflags +faststart -avoid_negative_ts make_zero "{{ $json.processing.paths.optimized }}"`;
} else {
  // Just add faststart without re-encoding
  ffmpegCommand = `ffmpeg -i "{{ $json.processing.paths.original }}" -c copy -movflags +faststart -avoid_negative_ts make_zero "{{ $json.processing.paths.optimized }}"`;
}

return [{
  json: {
    ...item,
    processing: {
      ...item.processing,
      videoInfo: {
        duration,
        bitrate,
        width,
        height,
        codec: videoStream.codec_name,
        hasAudio,
        needsReencoding
      },
      ffmpegCommand,
      step: 'optimization_planned'
    }
  }
}];

// Prepare final data for Telegram
const item = $input.first()?.json;
const optimizedBinary = $input.first()?.binary?.optimized_video;
const thumbnailBinary = $input.first()?.binary?.thumbnail;

if (!optimizedBinary) {
  throw new Error('Optimized video not found');
}

// Get file stats
const fs = require('fs');
let optimizedSize = 0;
try {
  const stats = fs.statSync(item.processing.paths.optimized);
  optimizedSize = stats.size;
} catch (e) {
  console.log('Could not get optimized file size');
}

// Check Telegram size limit
const telegramMaxSize = 50 * 1024 * 1024; // 50MB
if (optimizedSize > telegramMaxSize) {
  throw new Error(`Video too large for Telegram: ${Math.round(optimizedSize/1048576)}MB > 50MB`);
}

// Generate caption
const originalSizeMB = Math.round(item.processing.originalSize / 1048576 * 100) / 100;
const optimizedSizeMB = Math.round(optimizedSize / 1048576 * 100) / 100;
const compressionRatio = Math.round((1 - optimizedSize/item.processing.originalSize) * 100);

const caption = `üé¨ **${item.name}**\n\n` +
  `üìä **‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÑ‡∏ü‡∏•‡πå:**\n` +
  `‚Ä¢ ‡∏Ç‡∏ô‡∏≤‡∏î‡∏ï‡πâ‡∏ô‡∏â‡∏ö‡∏±‡∏ö: ${originalSizeMB}MB\n` +
  `‚Ä¢ ‡∏Ç‡∏ô‡∏≤‡∏î‡∏´‡∏•‡∏±‡∏á‡∏õ‡∏£‡∏∞‡∏°‡∏ß‡∏•‡∏ú‡∏•: ${optimizedSizeMB}MB\n` +
  `‚Ä¢ ‡∏≠‡∏±‡∏ï‡∏£‡∏≤‡∏ö‡∏µ‡∏ö‡∏≠‡∏±‡∏î: ${compressionRatio}%\n\n` +
  `üé• **‡∏Ñ‡∏∏‡∏ì‡∏™‡∏°‡∏ö‡∏±‡∏ï‡∏¥:**\n` +
  `‚Ä¢ ‡∏Ñ‡∏ß‡∏≤‡∏°‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î: ${item.processing.videoInfo?.width}x${item.processing.videoInfo?.height}\n` +
  `‚Ä¢ ‡∏£‡∏∞‡∏¢‡∏∞‡πÄ‡∏ß‡∏•‡∏≤: ${Math.round(item.processing.videoInfo?.duration || 0)}s\n` +
  `‚Ä¢ Codec: ${item.processing.videoInfo?.codec || 'N/A'}\n\n` +
  `‚úÖ ‡∏û‡∏£‡πâ‡∏≠‡∏°‡πÄ‡∏•‡πà‡∏ô‡∏ó‡∏±‡∏ô‡∏ó‡∏µ‡πÉ‡∏ô Telegram`;

return [{
  json: {
    ...item,
    telegram: {
      caption,
      chatId: process.env.TELEGRAM_CHAT_ID || item.telegram_chat_id,
      optimizedSizeMB,
      compressionRatio
    },
    processing: {
      ...item.processing,
      finalSize: optimizedSize,
      step: 'ready_for_telegram'
    }
  },
  binary: {
    optimized_video: optimizedBinary,
    thumbnail: thumbnailBinary
  }
}];

// Log successful processing
const item = $input.first()?.json;
const endTime = new Date().toISOString();
const startTime = item.processing?.startTime;

let processingTimeMs = 0;
if (startTime) {
  processingTimeMs = new Date(endTime) - new Date(startTime);
}

const logData = {
  fileName: item.name,
  originalSizeMB: Math.round(item.processing.originalSize / 1048576 * 100) / 100,
  finalSizeMB: item.telegram?.optimizedSizeMB || 0,
  compressionRatio: item.telegram?.compressionRatio || 0,
  processingTimeSeconds: Math.round(processingTimeMs / 1000),
  status: 'success',
  timestamp: endTime
};

console.log('Video processing completed:', JSON.stringify(logData, null, 2));

return [{
  json: {
    ...item,
    processing: {
      ...item.processing,
      endTime,
      processingTimeMs,
      status: 'completed'
    }
  }
}];
